mapper.insert:
INSERT INTO pb_res_image
( accountId, path, thumbPath, name, size, width, height, type )
VALUES (
	1, #{path}, #{thumbPath}, #{name}, #{size}, #{width}, #{height}, #{type}
)

mapper.update:
  UPDATE pb_res_image 
  SET 
    accountId = 1
    , path =#{path}
    , thumbPath =#{thumbPath}
    , name =#{name}
    , size =#{size}
    , width =#{width}
    , height =#{height}
	, type =#{type}
  where _id= #{ _id }
