mapper.update:
UPDATE pb_docs
SET title = #{ title } , description = #{ description }, projectId = #{ projectId }, postedDate = #{ postedDate }, 
	sceneList = #{ sceneList }, previewImage = #{ previewImage }, category = #{ category }
where _id= #{ _id }

mapper.select:
select *
from pb_docs 
where title= #{ title }

select *
from pb_docs 
where description= #{ description }
