mapper.insert:
INSERT INTO pb_project
( accountId, title, comment, createDate, modifyDate, sceneList, previewImage, width, height )
VALUES (
	#{accountId}, #{title}, #{comment}, #{createDate}, #{modifyDate}
	, #{sceneList}, #{previewImage}, #{width}, #{height}
)

mapper.update:
UPDATE pb_project 
SET 
  accountId =#{accountId} , title =#{title}, comment =#{comment}, 
  createDate =#{createDate}, modifyDate =#{modifyDate}, sceneList =#{sceneList},
  previewImage =#{previewImage}, width =#{width}, height =#{height}
where _id= #{ _id }
